import { useEffect, useRef, useState } from "react";
import { Card } from "@/components/ui/card";
import { createSortingVisualization, createSearchVisualization, createGraphVisualization } from "@/lib/d3-visualization";

interface ExecutionResult {
  success: boolean;
  algorithmType: string;
  executionTime: number;
  memoryUsage: number;
  steps: any[];
}

interface AlgorithmVisualizationProps {
  executionResult: ExecutionResult | null;
  currentStep: number;
  isPlaying: boolean;
}

export function AlgorithmVisualization({ 
  executionResult, 
  currentStep, 
  isPlaying 
}: AlgorithmVisualizationProps) {
  const svgRef = useRef<SVGSVGElement>(null);
  const [dimensions, setDimensions] = useState({ width: 600, height: 300 });

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      if (svgRef.current?.parentElement) {
        const rect = svgRef.current.parentElement.getBoundingClientRect();
        setDimensions({
          width: Math.max(400, rect.width - 32),
          height: 300
        });
      }
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (!executionResult || !svgRef.current) return;

    const svg = svgRef.current;
    const currentStepData = executionResult.steps[currentStep];

    // Clear previous visualization
    svg.innerHTML = '';

    try {
      switch (executionResult.algorithmType) {
        case 'sorting':
          createSortingVisualization(svg, currentStepData, dimensions);
          break;
        case 'searching':
          createSearchVisualization(svg, currentStepData, dimensions);
          break;
        case 'graph':
          createGraphVisualization(svg, currentStepData, dimensions);
          break;
        default:
          createDefaultVisualization(svg, dimensions);
      }
    } catch (error) {
      console.error('Visualization error:', error);
      createDefaultVisualization(svg, dimensions);
    }
  }, [executionResult, currentStep, dimensions]);

  if (!executionResult) {
    return (
      <div className="h-64 flex items-center justify-center bg-card border border-border rounded-lg">
        <div className="text-center text-muted-foreground">
          <div className="text-4xl mb-4">ðŸ“Š</div>
          <p>Execute your algorithm to see visualization</p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-64 bg-card border border-border rounded-lg overflow-hidden">
      <svg
        ref={svgRef}
        width={dimensions.width}
        height={dimensions.height}
        className="w-full h-full"
        viewBox={`0 0 ${dimensions.width} ${dimensions.height}`}
      />
    </div>
  );
}

function createDefaultVisualization(svg: SVGSVGElement, dimensions: { width: number; height: number }) {
  const g = document.createElementNS('http://www.w3.org/2000/svg', 'g');

  const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
  text.setAttribute('x', (dimensions.width / 2).toString());
  text.setAttribute('y', (dimensions.height / 2).toString());
  text.setAttribute('text-anchor', 'middle');
  text.setAttribute('fill', 'var(--muted-foreground)');
  text.setAttribute('font-size', '16');
  text.textContent = 'Algorithm visualization will appear here';

  g.appendChild(text);
  svg.appendChild(g);
}

client/src/components/execution-controls.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Slider } from "@/components/ui/slider";
import { Play, Pause, Square, SkipBack, SkipForward, PlayCircle } from "lucide-react";

interface ExecutionControlsProps {
  isPlaying: boolean;
  currentStep: number;
  maxSteps: number;
  animationSpeed: number[];
  onPlayPause: () => void;
  onStep: (direction: 'forward' | 'backward') => void;
  onReset: () => void;
  onSpeedChange: (speed: number[]) => void;
}

export function ExecutionControls({
  isPlaying,
  currentStep,
  maxSteps,
  animationSpeed,
  onPlayPause,
  onStep,
  onReset,
  onSpeedChange
}: ExecutionControlsProps) {
  return (
    <Card data-testid="execution-controls-card">
      <CardHeader>
        <CardTitle className="flex items-center text-base">
          <PlayCircle className="w-4 h-4 text-primary mr-2" />
          Execution Controls
        </CardTitle>
      </CardHeader>
      <CardContent>
        {/* Main Controls */}
        <div className="flex gap-2 mb-4">
          <Button
            variant={isPlaying ? "secondary" : "default"}
            size="sm"
            onClick={onPlayPause}
            disabled={maxSteps === 0}
            className="flex-1 bg-green-600 hover:bg-green-700 text-white"
            data-testid="button-play-pause"
          >
            {isPlaying ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
          </Button>
          <Button
            variant="secondary"
            size="sm"
            onClick={onReset}
            disabled={maxSteps === 0}
            className="flex-1"
            data-testid="button-reset"
          >
            <Square className="w-4 h-4" />
          </Button>
        </div>

        {/* Step Controls */}
        <div className="flex gap-2 mb-4">
          <Button
            variant="outline"
            size="sm"
            onClick={() => onStep('backward')}
            disabled={currentStep === 0 || maxSteps === 0}
            className="flex-1"
            data-testid="button-step-backward"
          >
            <SkipBack className="w-4 h-4 mr-1" />
            <span className="text-xs">Step Back</span>
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={() => onStep('forward')}
            disabled={currentStep >= maxSteps - 1 || maxSteps === 0}
            className="flex-1"
            data-testid="button-step-forward"
          >
            <span className="text-xs mr-1">Step</span>
            <SkipForward className="w-4 h-4" />
          </Button>
        </div>

        {/* Speed Control */}
        <div className="space-y-3">
          <Label className="text-sm font-medium text-muted-foreground">
            Animation Speed
          </Label>
          <Slider
            value={animationSpeed}
            onValueChange={onSpeedChange}
            max={10}
            min={1}
            step={1}
            className="w-full"
            data-testid="slider-animation-speed"
          />
          <div className="flex justify-between text-xs text-muted-foreground">
            <span>Slow</span>
            <span>Fast</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

