                  ) : (
                    <div className="text-muted-foreground" data-testid="no-output">
                      No execution results yet. Run your algorithm to see output here.
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

